class ShotiAPI {
   constructor(api_key) {
      this.api_key = api_key;
   }
   getShoti(obj) {
   	return new Promise((resolve, reject) => {
       	fetch(`https://shoti.libyzxy0-dev.repl.co/api?apikey=${this.api_key}&type=${obj.type}`)
           .then(response => response.json())
           .then(data => {
                resolve(data);
            })
           .catch(error => {
            	reject('Error:', error);
           })
       })
   }
}